import axios from 'axios';

const BASE_URL =
  process.env.REACT_APP_API_BASE ||
  'https://inventarioshalito.onrender.com/api';

const api = axios.create({
  baseURL: BASE_URL,
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json',
  },
});

export default api;
